const Index = (props) => (
  <div>
    <h1>Covid-19 Cases in Bangladesh</h1>
    <table style={"border-collapse: separate; border-spacing: 50px 0;"}>
      <tr>
        <th>Date</th>
        <th>Case of infected</th>
      </tr>
      {props.data.map((show) => (
        <tr>
          <td>{show.Date}</td>
          <td>{show.Cases}</td>
        </tr>
      ))}
    </table>
  </div>
);

Index.getInitialProps = async function () {
  const url =
    "https://api.covid19api.com/dayone/country/bangladesh/status/confirmed/live";
  const res = await fetch(url);
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    data: data.map((entry) => entry),
  };
};

export default Index;

