import React from "react";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import ProTip from "../components/ProTip";
import Link from "../components/Link";
import Copyright from "../components/Copyright";
import { Button } from "@material-ui/core";
// import Link from "next/link";
import fetch from "isomorphic-unfetch";

//import { XMLHttpRequest } from "../../node_modules/xmlhttprequest/lib/XMLHttpRequest";
//const RequestService = require("../../functions/utils.js");
// var XMLHttpRequest = require("xmlhttprequest").XMLHttpRequest;
//var requestService = new RequestService();
// var XMLHttpRequest = require("xmlhttprequest").XMLHttpRequest;
let url =
  "https://api.covid19api.com/dayone/country/south-africa/status/confirmed/live";

// var request = new XMLHttpRequest();
// request.open("GET", url);
// request.send(null);
// alert(request.status);

// let covidapi = httpGet(url);
// console.log(covidapi);

const Index = (props) => (
  <Layout>
    <h1>Batman TV Shows</h1>
    <ul>
      {props.shows.map((show) => (
        <li key={show.id}>
          <Link href="/p/[id]" as={`/p/${show.id}`}>
            <a>{show.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

Index.getInitialProps = async function () {
  const res = await fetch(url);
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    shows: data.map((entry) => entry.show),
  };
};

export default Index;

